import React, { useState, useEffect } from 'react'
import { Parallax } from 'react-scroll-parallax'
import styled from '@emotion/styled'
import './styles.css'
import {
  Link
} from "react-router-dom";
import heroBG from './images/hero-bg.jpg'
import trashBG from './images/trash-bg.jpg'
import logo from './images/logo-transparent.png'
const colors = {
  darkBlue: '#13337a',
  lightBlue: '#6daedf',
  red: '#ee2427',
}

const Home = () => {
  useEffect(() => {

  }, []);

  return (
    <div>
      <HeroSection bg={ trashBG }>
        <img src={logo} className="logo" alt="logo" />
        <HeroSectionInner>
          <h1><span>Truth About</span> Recycling</h1>
          <div className="video-container">
            <video playsInline autoPlay muted loop src={ trashBG } type="video/mp4"/>
            <button><Link to="/watch">Watch Now</Link></button>
          </div>
        </HeroSectionInner>
      </HeroSection>

      <Footer>
        <a target="_blank" href="https://nclnet.org/">
          National Consumers League
        </a>
        <p>For confidence and safety in the marketplace since 1899.</p>
      </Footer>
    </div>
  )
}

const HeroSection = styled.div`
  background: url( ${ props => props.bg }) no-repeat top;
  background-size: cover;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  position: relative;
  flex-direction: column;
  .logo {
    width: 150px;
    position: absolute;
    top: 20px;
    left: 10px;
  }
`

const HeroSectionInner = styled.div`
  padding: 0 40px;
  display: flex;
  flex-direction: column;
  margin-top: 150px;
  h1 {
    font-size: 100px;
    font-family: 'black';
    color: ${ colors.darkBlue };
    text-transform: uppercase;
  }
  span {
    color: ${ colors.lightBlue };
    display: block;
    font-size: 60px;
    text-transform: initial;
  }
  .video-container {
    position: relative;
    width: 700px;
  }
  img {
    width: 100%;
  }
  video {
    position: absolute;
    top: 50%;
    left: 50px;
    transform: translateX(0) translateY(-50%);
    width: 400px;
    height: auto;
    overflow: hidden;
  video {
  }
  video {
    position: absolute;
    top: 50%;
    left: 50px;
    transform: translateX(0) translateY(-50%);
    width: 400px;
    height: auto;
    overflow: hidden;
    z-index: 1;
  }
  button {
    cursor: pointer;
    position: absolute;
    left: 0;
    background: ${ colors.red };
    border: none;
    padding: 10px;
    color: #fff;
    font-family: 'black';
    z-index: 100;
    top: 50%;
  }
`


const Footer = styled.footer`
  background: ${ colors.darkBlue };
  text-align: center;
  padding: 10px;
  a {
    color: ${ colors.lightBlue };
    font-family: 'medium';
    margin-bottom: 10px;
    display: inline-block;
  }
  p {
    color: #fff;
    font-size: 14px;
    font-family: 'light';
  }
`


export default Home
