import React, { useState, useEffect } from 'react'
import { Parallax } from 'react-scroll-parallax'
import styled from '@emotion/styled'
import './styles.css'
import {
  Link
} from "react-router-dom";

const colors = {
  darkBlue: '#13337a',
  lightBlue: '#6daedf',
  red: '#ee2427',
}

const Home = () => {
  useEffect(() => {

  }, []);

  return (
    <div>
      <HeroSection bg={ hero }>
        <img src={logo} className="logo" alt="logo" />
        <HeroSectionInner>
          <h1><span>Truth About</span> Recycling</h1>
          <div className="video-container">
            <video playsInline autoPlay muted loop src={ video } type="video/mp4"/>
            <img className="background" src={ bottle } alt="National Consumer League"/>
            <button><Link to="/watch">Watch Now</Link></button>
          </div>
        </HeroSectionInner>
      </HeroSection>

      <Footer>
        <a target="_blank" href="https://nclnet.org/">
          National Consumers League
        </a>
        <p>For confidence and safety in the marketplace since 1899.</p>
      </Footer>
    </div>
  )
}

const HeroSection = styled.div`
  background: url( ${ props => props.bg }) no-repeat top;
  background-size: cover;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  position: relative;
  flex-direction: column;
  .logo {
    width: 150px;
    position: absolute;
    top: 20px;
    left: 10px;
  }
`

const Footer = styled.footer`
  background: ${ colors.darkBlue };
  text-align: center;
  padding: 10px;
  a {
    color: ${ colors.lightBlue };
    font-family: 'medium';
    margin-bottom: 10px;
    display: inline-block;
  }
  p {
    color: #fff;
    font-size: 14px;
    font-family: 'light';
  }
`


export default Home
