import React, { useState, useEffect } from 'react'
import { Parallax } from 'react-scroll-parallax'
import styled from '@emotion/styled'
import './styles.css'
import {
  Link
} from "react-router-dom"
import heroBG from './images/hero-bg.jpg'
import trashBG from './images/trash-bg.jpg'
import logo from './images/logo-transparent.png'
import read from './images/read.png'
import listen from './images/listen.png'
import examine from './images/examine.png'
const colors = {
  darkBlue: '#13337a',
  lightBlue: '#6daedf',
  red: '#ee2427',
}

const boxes = [
  {
    img: read,
    link: 'https://nclnet.org/wp-content/uploads/2020/10/NCL-Recycling-Report-Oct-2020.pdf',
    cta: 'READ',
    description: 'NCL’s report, Examining Sustainability, Consumer Choice, and Confusion in Food and Beverage Packaging'
  },
  {
    img: listen,
    link: 'https://nclnet.org/does-recycling-even-work/',
    cta: 'LISTEN',
    description: 'NCL’s “We Can Do This!” podcast episode, “Does recycling even work?”'
  },
  {
    img: examine,
    link: 'https://nclnet.org/national_recycling_day/',
    cta: 'EXAMINE',
    description: 'NCL’s infographic about how to become a savvy recycler and sustainable shopper'
  },
]


const Home = () => {
  useEffect(() => {

  }, []);
//<button><Link to="/watch">Watch Now</Link></button>

  return (
    <div>
      <HeroSection bg={ trashBG }>
        <HeroSectionInner>
          <img src={logo} className="logo" alt="logo" />
          <h1><span>Facts About</span> Recycling</h1>
          <HeroBoxes>
            { boxes.map( b =>
              <div className="box" key={ b.cta }>
                <div className="box-image">
                  <a href="https://nclnet.org/wp-content/uploads/2020/10/NCL-Recycling-Report-Oct-2020.pdf">
                    <img src={ b.img } alt="National Consumer League"/>
                    <button>{ b.cta }</button>
                  </a>
                </div>
                <p className="description">{ b.description }</p>
              </div>
            )}
          </HeroBoxes>
        </HeroSectionInner>
      </HeroSection>

      <Footer>
        <a target="_blank" href="https://nclnet.org/">
          National Consumers League
        </a>
        <p>For confidence and safety in the marketplace since 1899.</p>
      </Footer>
    </div>
  )
}

const HeroSection = styled.div`
  background: url( ${ heroBG }) no-repeat top;
  background-size: cover;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  position: relative;
  flex-direction: column;
  min-height: 800px;
`

const HeroSectionInner = styled.div`
  padding: 0 40px;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 150px;
  .logo {
    margin-bottom: 30px;
    width: 140px;
  }
  h1 {
    font-size: 80px;
    font-family: 'black';
    text-align: center;
    color: ${ colors.darkBlue };
    text-transform: uppercase;
  }
  span {
    color: ${ colors.lightBlue };
    display: block;
    font-size: 50px;
    text-transform: initial;
  }
  .video-container {
    position: relative;
    width: 700px;
  }
`
//
// <div className="box" key={ b.cta }>
//   <div className="box-image">
//     <img src={ b.img } alt="National Consumer League"/>
//     <a href="https://nclnet.org/wp-content/uploads/2020/10/NCL-Recycling-Report-Oct-2020.pdf">{ b.cta }</a>
//   </div>
//   { b.description }
// </div>

const HeroBoxes = styled.div`
  display: flex;
  margin-top: 40px;
  width: 100%;
  max-width: 800px;
  justify-content: space-between;
  .box {
    max-width: 200px;
  }
  .box-image {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    img {
      width: 100%;
    }
  }
  button {
    border: none;
    background: ${ colors.red };
    padding: 10px;
    display: inline-block;
    color: #fff;
    font-family: 'black';
    text-decoration: none;
    border-radius: 4px;
    width: 100px;
    text-align: center;
    margin-top: -20px;
  }
  .description {
    font-family: 'regular';
    color: ${ colors.darkBlue };
    text-align: center;
    margin-top: 20px;
    line-height: 1.4;
    font-size: 15px;
  }
`

const Footer = styled.footer`
  background: ${ colors.darkBlue };
  text-align: center;
  padding: 10px;
  a {
    color: ${ colors.lightBlue };
    font-family: 'medium';
    margin-bottom: 10px;
    display: inline-block;
  }
  p {
    color: #fff;
    font-size: 14px;
    font-family: 'light';
  }
`


export default Home
